<script>

function getCurrentRotation(el){
        var st = window.getComputedStyle(el, null);
        var tm = st.getPropertyValue("-webkit-transform") ||
                st.getPropertyValue("-moz-transform") ||
                st.getPropertyValue("-ms-transform") ||
                st.getPropertyValue("-o-transform") ||
                st.getPropertyValue("transform") ||
                "none";
        if (tm != "none") {
            var values = tm.split('(')[1].split(')')[0].split(',');
            
            var angle = Math.round(Math.atan2(values[1],values[0]) * (180/Math.PI));
            return (angle < 0 ? angle + 360 : angle); //adding 360 degrees here when angle < 0 is equivalent to adding (2 * Math.PI) radians before
        }
        return 0;
}


const svg = document.querySelector("#svg_1");
let degAdd = 1;
let gravity = 2;
let degTo = degAdd;
let nowVal = null;
let speed = 200;
//현재 각 계산 180~360 은 -180 ~ 0 으로 치환시킨다
function calCurrentDeg() {

    if(getCurrentRotation(svg) < 180){

    nowVal = getCurrentRotation(svg);
    }else{

    nowVal = getCurrentRotation(svg) - 360;
    }
}

let transforms = function(degVal){
    svg.style.transform = "rotate("+degVal+"deg)";
}
    
let repeatMoon = function(){
    let repeat = setInterval(function(){

        

        if(nowVal > 0) {
            
            nowVal-=gravity;
            transforms(nowVal);
            setTimeout(function(){
                transforms(-nowVal);
            }, speed);
            console.log(nowVal);
        } else {
            clearInterval(repeat);
            
        }
    }, speed * 2);
}



function moveLeftSide(degTo) {
    nowVal = getCurrentRotation(svg);
    
    const value = `rotate(${degTo}deg)`;
    svg.style.transform = value;
    
    console.log(degTo);
}

function moveRightSide(degTo) {
    nowVal = getCurrentRotation(svg);
    
    
    const value = `rotate(${degTo}deg)`;
    svg.style.transform = value;
    
    console.log(degTo);
}

function degAddFunc() {
    clearInterval(repeatMoon.repeat);
   
    nowVal = getCurrentRotation(svg);
    
    if(nowVal >= 0){
        degTo += degAdd;
        moveLeftSide(degTo);
    }else{
        degTo -= degAdd;
        moveRightSide(degTo);
    }
    
    
    
    
    
}


let id = document.querySelector("input[type='text']");
svg.style.transition = "all " + (speed / 1000) + "s";
svg.style.transformOrigin = "50% 0%";
id.addEventListener("keyup", degAddFunc);
id.addEventListener( 'blur', repeatMoon);


</script>

